","keccak256":"0x4a78cc71a6aac6f7174a9da2b3f481f42ee83ade33e40240573081e8b0f3c628","license":"MIT"},"contracts/Pool.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.24;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Create2} from "@openzeppelin/contracts/utils/Create2.sol";
import {Field, ImplField} from "./libraries/Field.sol";
import {SplitJoin16Verifier, Hash2Verifier, NoteVerifier} from "./Verifier.sol";
import {StealthAddress} from "./StealthAddress.sol";
import {MerkleTreeWithHistory} from "./MerkleTreeWithHistory.sol";
import "hardhat/console.sol";

contract Pool is MerkleTreeWithHistory {
    using ImplField for Field;
    using ImplField for uint256;
    using ImplField for int256;
    using ImplField for bytes32;
    using ImplField for address;
    using ImplField for Field[];

    Field constant FIELD_ZERO = Field.wrap(0);

    Field public constant ZERO_ROOT =
        Field.wrap(
            0x087486f7f14f265e263a4a6e776d45c15664d2dcb8c72288f4acf7fe1daeedaf
        );
    Field public constant ZERO_COMMITMENT =
        Field.wrap(
            0x0ecc1f56d6a29051a511ea4b08361649d190b7f7525a9d4ed36b9041e127207a
        );
    Field public constant ZERO_NULLIFIER =
        Field.wrap(
            0x262540451c6dd240fa72641be24bca61b14a3e5101df5633ef8d4f9e669eddb2
        );

    bytes32 public constant INIT_CODE_HASH =
        keccak256(type(StealthAddress).creationCode);

    IERC20 public usdc;
    SplitJoin16Verifier public splitJoinVerifier;
    Hash2Verifier public hash2Verifier;
    NoteVerifier public noteVerifier;

    mapping(Field nullifier => bool isSpent) public isNoteSpent;
    Field[] public noteCommitments;

    event NewCommitment(Field commitment);
    event NullifierSpent(Field commitment);

    constructor(
        SplitJoin16Verifier _splitJoinVerifier,
        Hash2Verifier _hash2Verifier,
        NoteVerifier _noteVerifier,
        IERC20 _usdc
    ) MerkleTreeWithHistory(16) {
        splitJoinVerifier = _splitJoinVerifier;
        hash2Verifier = _hash2Verifier;
        noteVerifier = _noteVerifier;
        usdc = _usdc;
        noteCommitments.push(ZERO_COMMITMENT);
        _insert(ZERO_COMMITMENT);
    }

    function deposit(
        uint amount,
        Field noteCommitment,
        bytes memory proof
    ) external {
        _createDepositUsingHash3(amount, noteCommitment, proof);
    }

    // proving split join takes about 5 seconds
    function transact(
        bytes memory proof,
        Field[] memory publicInputs
    ) external {
        // verify that the note has not been spent
        require(isKnownRoot(publicInputs[0]), "Deposits root unknown");
        (bool isDeposit, uint amount) = publicInputs[1].signed();

        if (!publicInputs[3].eq(ZERO_NULLIFIER)) {
            require(!isNoteSpent[publicInputs[3]], "Note is spent");
            isNoteSpent[publicInputs[3]] = true;
            emit NullifierSpent(publicInputs[3]);
        }
        if (!publicInputs[4].eq(ZERO_NULLIFIER)) {
            require(!isNoteSpent[publicInputs[4]], "Note is spent");
            isNoteSpent[publicInputs[4]] = true;
            emit NullifierSpent(publicInputs[4]);
        }

        require(
            splitJoinVerifier.verify(proof, publicInputs.into()),
            "split join zk proof failed"
        );

        // insert note in merkle tree and calculate new root
        emit NewCommitment(publicInputs[4]);
        noteCommitments.push(publicInputs[4]);
        _insert(publicInputs[4]);

        if (isDeposit) {
            // pull USDC
            usdc.transferFrom(msg.sender, address(this), amount);
        } else {
            // transfer USDC
            usdc.transfer(publicInputs[2].toAddress(), amount);
        }
    }

    function collect(
        IERC20 token,
        uint balance,
        Field salt,
        bytes memory stealthAddressOwnershipZkProof,
        Field noteCommitment,
        bytes memory noteCreationZkProof
    ) external {
        require(
            hash2Verifier.verify(stealthAddressOwnershipZkProof, salt.toArr()),
            "hash2 zk proof failed"
        );
        StealthAddress wallet = _deployIfNeeded(salt.toBytes32());
        wallet.transferErc20(token, address(this), balance);
        _createDepositUsingHash3(balance, noteCommitment, noteCreationZkProof);
    }

    function compute(Field salt) public view returns (address stealthAddress) {
        return Create2.computeAddress(salt.toBytes32(), INIT_CODE_HASH);
    }

    function allNoteCommitments() public view returns (Field[] memory) {
        return noteCommitments;
    }

    function noteCommitmentsLength() public view returns (uint256) {
        return noteCommitments.length;
    }

    function noteCommitmentsPaginated(uint start, uint length) public view returns (Field[] memory) {
        Field[] memory result = new Field[](length);
        for (uint i = 0; i < length; i++) {
            result[i] = noteCommitments[start + i];
        }
        return result;
    }

    function _deployIfNeeded(bytes32 salt) internal returns (StealthAddress) {
        address computed = Create2.computeAddress(salt, INIT_CODE_HASH);
        uint size;
        assembly {
            size := extcodesize(computed)
        }
        if (size == 0) {
            bytes memory bytecode = type(StealthAddress).creationCode;
            address deployed;
            assembly {
                deployed := create2(
                    0,
                    add(bytecode, 0x20),
                    mload(bytecode),
                    salt
                )
            }
            require(
                deployed != address(0),
                "WalletFactory: failed to deploy wallet"
            );
            require(deployed == computed, "WalletFactory: deploy mismatch");
        }
        return StealthAddress(payable(computed));
    }

    // proving note takes about 1 sec
    function _createDepositUsingHash3(
        uint amount,
        Field noteCommitment,
        bytes memory proof
    ) internal {
        // verify that amount is in note commitment preimage using the zk proof
        Field[] memory publicInputs = new Field[](2);
        publicInputs[0] = amount.toField();
        publicInputs[1] = noteCommitment;

        require(
            noteVerifier.verify(proof, publicInputs.into()),
            "note zk proof failed"
        );

        // insert note in merkle tree and calculate new root
        // TODO this requires poseidon2 to be implemented in solidity,
        // temporarily taking this from solidity
        // proving the new deposit root will cause mutex issue hence
        // we have to do poseidon2 in solidity to allow parallel users.
        noteCommitments.push(noteCommitment);
        _insert(noteCommitment);
    }

    // proving split join takes about 5 seconds
    function _createDepositUsingSplitJoin(
        uint amount,
        Field noteCommitment,
        bytes memory proof
    ) internal {
        // verify that amount is in note commitment preimage using the zk proof
        Field[] memory publicInputs = new Field[](6);
        publicInputs[0] = ZERO_ROOT;
        publicInputs[1] = amount.toField();
        publicInputs[2] = FIELD_ZERO;
        publicInputs[3] = ZERO_NULLIFIER;
        publicInputs[4] = ZERO_NULLIFIER;
        publicInputs[5] = noteCommitment;

        require(
            splitJoinVerifier.verify(proof, publicInputs.into()),
            "split join zk proof failed"
        );

        // insert note in merkle tree and calculate new root
        // TODO this requires poseidon2 to be implemented in solidity,
        // temporarily taking this from solidity
        // proving the new deposit root will cause mutex issue hence
        // we have to do poseidon2 in solidity to allow parallel users.
        noteCommitments.push(noteCommitment);
        _insert(noteCommitment);
    }
}
","keccak256":"0xf540efcd2c135a8f07820f009aea9bd0f15fbceedf3336b9a31cae477414203b","license":"UNLICENSED"},"contracts/StealthAddress.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.24;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract StealthAddress {
    address public pool;

    constructor() {
        pool = msg.sender;
    }

    modifier onlyPool() {
        require(msg.sender == pool, "only factory");
        _;
    }

    receive() external payable {}

    function transferErc20(
        IERC20 token,
        address dest,
        uint amount
    ) external onlyPool returns (bool success) {
        return token.transfer(dest, amount);
    }

    function call(
        address dest,
        uint value,
        bytes calldata data
    ) external onlyPool returns (bool success, bytes memory returndata) {
        (success, returndata) = dest.call{value: value}(data);
    }
}
","keccak256":"0x89b5680e201e061ad7102ef74bd6385b2a672b91330be9e501fc3e7c2af59853","license":"UNLICENSED"},"contracts/Verifier.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.24;

import {UltraVerifier as SplitJoin16Verifier_} from "@ultralane/circuits/bin/split_join_16/contract/split_join_16/plonk_vk.sol";
import {UltraVerifier as SplitJoin32Verifier_} from "@ultralane/circuits/bin/split_join_32/contract/split_join_32/plonk_vk.sol";
import {UltraVerifier as Hash2Verifier_} from "@ultralane/circuits/bin/hash_2/contract/hash_2/plonk_vk.sol";
import {UltraVerifier as NoteVerifier_} from "@ultralane/circuits/bin/note/contract/note/plonk_vk.sol";

contract SplitJoin16Verifier is SplitJoin16Verifier_ {}

contract SplitJoin32Verifier is SplitJoin32Verifier_ {}

contract Hash2Verifier is Hash2Verifier_ {}

contract NoteVerifier is NoteVerifier_ {}
","keccak256":"0x773b2a82fa72bd3873170d7c921a455b6fd60b768d24c88c5e9a605b32eea73f","license":"UNLICENSED"},"contracts/libraries/Field.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.24;

type Field is uint256;

library ImplField {
    uint constant PRIME =
        0x30644e72e131a029b85045b68181585d2833e84879b9709143e1f593f0000001;
    uint constant PRIME_DIV_2 =
        0x183227397098d014dc2822db40c0ac2e9419f4243cdcb848a1f0fac9f8000000;

    function checkField(Field a) internal pure {
        require(Field.unwrap(a) < PRIME, "Field: input is too large");
    }

    function toFieldUnchecked(uint256 a) internal pure returns (Field b) {
        b = Field.wrap(a);
    }
    function toField(uint256 a) internal pure returns (Field b) {
        b = Field.wrap(a);
        checkField(b);
    }

    function toFieldUnchecked(bytes32 a) internal pure returns (Field b) {
        assembly {
            b := a
        }
    }
    function toField(bytes32 a) internal pure returns (Field b) {
        assembly {
            b := a
        }
        checkField(b);
    }

    function toBytes32(Field a) internal pure returns (bytes32 b) {
        assembly {
            b := a
        }
    }

    function toAddress(Field a) internal pure returns (address b) {
        require(Field.unwrap(a) < (1 << 160), "Field: input is too large");
        assembly {
            b := a
        }
    }

    function toArr(Field a) internal pure returns (bytes32[] memory b) {
        b = new bytes32[](1);
        b[0] = toBytes32(a);
    }

    function toField(address a) internal pure returns (Field b) {
        assembly {
            b := a
        }
    }

    function toField(int256 a) internal pure returns (Field) {
        // return Field.wrap(a);
        if (a < 0) {
            require(uint(-a) < PRIME, "Field: input is too large");
            return Field.wrap(PRIME - uint256(-a));
        } else {
            require(uint(a) < PRIME, "Field: input is too large");
            return Field.wrap(uint256(a));
        }
    }

    function into(Field[] memory a) internal pure returns (bytes32[] memory b) {
        assembly {
            b := a
        }
    }

    function add(Field a, Field b) internal pure returns (Field c) {
        assembly {
            c := addmod(a, b, PRIME)
        }
    }

    function mul(Field a, Field b) internal pure returns (Field c) {
        assembly {
            c := mulmod(a, b, PRIME)
        }
    }

    function add(Field a, uint b) internal pure returns (Field c) {
        assembly {
            c := addmod(a, b, PRIME)
        }
    }

    function mul(Field a, uint b) internal pure returns (Field c) {
        assembly {
            c := mulmod(a, b, PRIME)
        }
    }

    function eq(Field a, Field b) internal pure returns (bool c) {
        assembly {
            c := eq(a, b)
        }
    }

    function isZero(Field a) internal pure returns (bool c) {
        assembly {
            c := eq(a, 0)
        }
    }

    function signed(
        Field a
    ) internal pure returns (bool positive, uint scalar) {
        uint256 raw = Field.unwrap(a);
        if (raw > PRIME_DIV_2) {
            return (false, PRIME - raw);
        } else {
            return (true, raw);
        }
    }
}
","keccak256":"0xd502d38a494a495f33e3dd385774ed6dc69d56d5c10bddad60c41b08a1dc2fd9","license":"UNLICENSED"},"contracts/libraries/Poseidon2.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.24;

import {Field, ImplField} from "./Field.sol";

library Poseidon2 {
    using ImplField for Field;
    using Poseidon2 for Sponge;

    uint constant t = 4;
    uint constant rounds_f = 8;
    uint constant rounds_p = 56;
    uint constant RATE = 3;

    struct Constants {
        Field[4] internal_matrix_diagonal;
        Field[4][64] round_constant;
    }

    struct Sponge {
        Field iv;
        Field[3] cache;
        Field[4] state;
        uint cache_size;
        bool squeeze_mode; // 0 => absorb, 1 => squeeze
        Constants constants;
    }

    /**
     * Public API: best for single time use
     */

    function hash_1(Field m) internal pure returns (Field) {
        Field[] memory inputs = new Field[](1);
        inputs[0] = m;
        return hash_internal(load(), inputs, 1, false);
    }

    function hash_2(Field m1, Field m2) internal pure returns (Field) {
        Field[] memory inputs = new Field[](2);
        inputs[0] = m1;
        inputs[1] = m2;
        return hash_internal(load(), inputs, 2, false);
    }

    function hash_3(
        Field m1,
        Field m2,
        Field m3
    ) internal pure returns (Field) {
        Field[] memory inputs = new Field[](3);
        inputs[0] = m1;
        inputs[1] = m2;
        inputs[2] = m3;
        return hash_internal(load(), inputs, 3, false);
    }

    function hash_var_len(
        Field[] memory inputs,
        uint std_input_length
    ) internal pure returns (Field) {
        return hash_internal(load(), inputs, std_input_length, true);
    }

    /**
     * Public API: best for multiple use in same call context
     */

    function hash_1(
        Poseidon2.Constants memory constants,
        Field m
    ) internal pure returns (Field) {
        Field[] memory inputs = new Field[](1);
        inputs[0] = m;
        return hash_internal(constants, inputs, 1, false);
    }

    function hash_2(
        Poseidon2.Constants memory constants,
        Field m1,
        Field m2
    ) internal pure returns (Field) {
        Field[] memory inputs = new Field[](2);
        inputs[0] = m1;
        inputs[1] = m2;
        return hash_internal(constants, inputs, 2, false);
    }

    function hash_3(
        Poseidon2.Constants memory constants,
        Field m1,
        Field m2,
        Field m3
    ) internal pure returns (Field) {
        Field[] memory inputs = new Field[](3);
        inputs[0] = m1;
        inputs[1] = m2;
        inputs[2] = m3;
        return hash_internal(constants, inputs, 3, false);
    }

    function hash_var_len(
        Poseidon2.Constants memory constants,
        Field[] memory inputs,
        uint std_input_length
    ) internal pure returns (Field) {
        return hash_internal(constants, inputs, std_input_length, true);
    }

    /**
     * Internal methods for hashing
     */

    // function hash_internal(
    //     Poseidon2.Sponge memory sponge,
    //     Field[] memory input,
    //     uint std_input_length,
    //     bool is_variable_length
    // ) private pure returns (Field) {
    //     for (uint i; i < input.length; i++) {
    //         if (i < std_input_length) {
    //             sponge.absorb(input[i]);
    //         }
    //     }

    //     // In the case where the hash preimage is variable-length, we append `1` to the end of the input, to distinguish
    //     // from fixed-length hashes. (the combination of this additional field element + the hash IV ensures
    //     // fixed-length and variable-length hashes do not collide)
    //     if (is_variable_length) {
    //         sponge.absorb(Field.wrap(1));
    //     }
    //     return sponge.squeeze();
    // }

    function generate_iv(uint input_length) internal pure returns (Field) {
        return Field.wrap(input_length << 64);
    }

    function hash_internal(
        Constants memory constants,
        Field[] memory input,
        uint std_input_length,
        bool is_variable_length
    ) private pure returns (Field) {
        Poseidon2.Sponge memory sponge = new_poseidon2(
            generate_iv(input.length),
            constants
        );

        for (uint i; i < input.length; i++) {
            if (i < std_input_length) {
                sponge.absorb(input[i]);
            }
        }

        // In the case where the hash preimage is variable-length, we append `1` to the end of the input, to distinguish
        // from fixed-length hashes. (the combination of this additional field element + the hash IV ensures
        // fixed-length and variable-length hashes do not collide)
        if (is_variable_length) {
            sponge.absorb(Field.wrap(1));
        }
        return sponge.squeeze();
    }

    function new_poseidon2(
        Field iv,
        Constants memory constants
    ) private pure returns (Poseidon2.Sponge memory) {
        Poseidon2.Sponge memory result = Poseidon2.Sponge({
            iv: iv,
            cache: [Field.wrap(0), Field.wrap(0), Field.wrap(0)],
            state: [Field.wrap(0), Field.wrap(0), Field.wrap(0), Field.wrap(0)],
            cache_size: 0,
            squeeze_mode: false,
            constants: constants
        });
        result.state[RATE] = iv;
        return result;
    }

    function new_poseidon2_with_constants(
        Field iv,
        Constants memory constants
    ) private pure returns (Poseidon2.Sponge memory) {
        Poseidon2.Sponge memory result = Poseidon2.Sponge({
            iv: iv,
            cache: [Field.wrap(0), Field.wrap(0), Field.wrap(0)],
            state: [Field.wrap(0), Field.wrap(0), Field.wrap(0), Field.wrap(0)],
            cache_size: 0,
            squeeze_mode: false,
            constants: constants
        });
        result.state[RATE] = iv;
        return result;
    }

    function perform_duplex(
        Poseidon2.Sponge memory self
    ) internal pure returns (Field[RATE] memory) {
        // zero-pad the cache
        for (uint i; i < RATE; i++) {
            if (i >= self.cache_size) {
                self.cache[i] = Field.wrap(0);
            }
        }

        // add the cache into sponge state
        for (uint i; i < RATE; i++) {
            self.state[i] = self.state[i].add(self.cache[i]);
        }
        self.state = permutation(
            self.state,
            self.constants.internal_matrix_diagonal,
            self.constants.round_constant
        );
        // return `RATE` number of field elements from the sponge state.
        Field[RATE] memory result = [
            Field.wrap(0),
            Field.wrap(0),
            Field.wrap(0)
        ];
        for (uint i; i < RATE; i++) {
            result[i] = self.state[i];
        }
        return result;
    }

    function absorb(Poseidon2.Sponge memory self, Field input) internal pure {
        if ((!self.squeeze_mode) && (self.cache_size == RATE)) {
            // If we're absorbing, and the cache is full, apply the sponge permutation to compress the cache
            self.perform_duplex();
            self.cache[0] = input;
            self.cache_size = 1;
        } else if ((!self.squeeze_mode) && (self.cache_size != RATE)) {
            // If we're absorbing, and the cache is not full, add the input into the cache
            self.cache[self.cache_size] = input;
            self.cache_size += 1;
        } else if (self.squeeze_mode) {
            // If we're in squeeze mode, switch to absorb mode and add the input into the cache.
            // N.B. I don't think this code path can be reached?!
            self.cache[0] = input;
            self.cache_size = 1;
            self.squeeze_mode = false;
        }
    }

    function squeeze(
        Poseidon2.Sponge memory self
    ) internal pure returns (Field) {
        if (self.squeeze_mode && (self.cache_size == 0)) {
            // If we're in squeze mode and the cache is empty, there is nothing left to squeeze out of the sponge!
            // Switch to absorb mode.
            self.squeeze_mode = false;
            self.cache_size = 0;
        }
        if (!self.squeeze_mode) {
            // If we're in absorb mode, apply sponge permutation to compress the cache, populate cache with compressed
            // state and switch to squeeze mode. Note: this code block will execute if the previous `if` condition was
            // matched
            Field[RATE] memory new_output_elements = self.perform_duplex();
            self.squeeze_mode = true;
            for (uint i; i < RATE; i++) {
                self.cache[i] = new_output_elements[i];
            }
            self.cache_size = RATE;
        }
        // By this point, we should have a non-empty cache. Pop one item off the top of the cache and return it.
        Field result = self.cache[0];
        for (uint i = 1; i < RATE; i++) {
            if (i < self.cache_size) {
                self.cache[i - 1] = self.cache[i];
            }
        }

        self.cache_size -= 1;
        self.cache[self.cache_size] = Field.wrap(0);
        return result;
    }

    function permutation(
        Field[4] memory inputs,
        Field[4] memory internal_matrix_diagonal,
        Field[4][64] memory round_constant
    ) private pure returns (Field[4] memory) {
        // Read witness assignments
        Field[4] memory state = [
            Field.wrap(0),
            Field.wrap(0),
            Field.wrap(0),
            Field.wrap(0)
        ];
        for (uint i; i < 4; i++) {
            state[i] = inputs[i];
        }

        // Apply 1st linear layer
        matrix_multiplication_4x4(state);

        // First set of external rounds
        uint rf_first = rounds_f / 2;
        for (uint r; r < rf_first; r++) {
            add_round_constants(state, round_constant, r);
            s_box(state);
            matrix_multiplication_4x4(state);
        }

        // Internal rounds
        uint p_end = rf_first + rounds_p;
        for (uint r = rf_first; r < p_end; r++) {
            state[0] = state[0].add(round_constant[r][0]);
            state[0] = single_box(state[0]);
            internal_m_multiplication(state, internal_matrix_diagonal);
        }

        // Remaining external rounds
        uint num_rounds = rounds_f + rounds_p;

        for (uint r = p_end; r < num_rounds; r++) {
            add_round_constants(state, round_constant, r);
            s_box(state);
            matrix_multiplication_4x4(state);
        }

        return state;
    }

    function single_box(Field x) private pure returns (Field) {
        Field s = x.mul(x);
        return s.mul(s).mul(x);
    }

    function s_box(Field[4] memory input) private pure {
        for (uint i; i < 4; i++) {
            input[i] = single_box(input[i]);
        }
    }

    function add_round_constants(
        Field[4] memory state,
        Field[4][64] memory round_constant,
        uint round
    ) private pure {
        for (uint i; i < 4; i++) {
            state[i] = state[i].add(round_constant[round][i]);
        }
    }

    function matrix_multiplication_4x4(Field[4] memory input) private pure {
        Field t0 = input[0].add(input[1]); // A + B
        Field t1 = input[2].add(input[3]); // C + D
        Field t2 = input[1].add(input[1]); // 2B
        t2 = t2.add(t1); // 2B + C + D
        Field t3 = input[3].add(input[3]); // 2D
        t3 = t3.add(t0); // 2D + A + B
        Field t4 = t1.add(t1);
        t4 = t4.add(t4);
        t4 = t4.add(t3); // A + B + 4C + 6D
        Field t5 = t0.add(t0);
        t5 = t5.add(t5);
        t5 = t5.add(t2); // 4A + 6B + C + D
        Field t6 = t3.add(t5); // 5A + 7B + C + 3D
        Field t7 = t2.add(t4); // A + 3B + 5C + 7D
        input[0] = t6;
        input[1] = t5;
        input[2] = t7;
        input[3] = t4;
    }

    function internal_m_multiplication(
        Field[4] memory input,
        Field[4] memory internal_matrix_diagonal
    ) private pure {
        Field sum = Field.wrap(0);
        for (uint i; i < 4; i++) {
            sum = sum.add(input[i]);
        }
        for (uint i; i < 4; i++) {
            input[i] = input[i].mul(internal_matrix_diagonal[i]);
            input[i] = input[i].add(sum);
        }
    }

